graphql.query:
mutation ($payload:AdminCreateResourceInput!)
{
	adminCreateResource (payload:$payload)
	{
		id
		name
		hasCustomFields
		hasAttachments
		createdAt
		updatedAt
	}
}

query ($query:QueryStatement $constraint:QueryStatement)
{
	adminPaginateResources (query:$query constraint:$constraint)
	{
		total
		count
		rows
	}
}

query ($query:QueryStatement)
{
	adminGetResources (query: $query)
	{
		id
		name
		hasCustomFields
		hasAttachments
		createdAt
		updatedAt
	}
}

query ($query:QueryStatement)
{
	adminFindResource (query: $query)
	{
		id
		name
		hasCustomFields
		hasAttachments
		createdAt
		updatedAt
	}
}

query ($id:ID)
{
	adminFindResourceById (id:$id)
	{
		id
		name
		hasCustomFields
		hasAttachments
		createdAt
		updatedAt
	}
}

mutation ($payload:AdminUpdateResourceInput!)
{
	adminUpdateResource (payload:$payload)
	{
		id
		name
		hasCustomFields
		hasAttachments
		createdAt
		updatedAt
	}
}

mutation ($id:ID!)
{
	adminDeleteResourceById (id:$id)
	{
		id
		name
		hasCustomFields
		hasAttachments
		createdAt
		updatedAt
	}
}

mutation ($query:QueryStatement)
{
	adminDeleteResources (query: $query)
	{
		id
		name
		hasCustomFields
		hasAttachments
		createdAt
		updatedAt
	}
}
