graphql.query:
query ($query:[QueryStatementInput] $constraint:[QueryStatementInput])
{
	adminPaginateRoles (query:$query constraint:$constraint)
	{
		total
		count
		rows
	}
}

query ($query:[QueryStatementInput])
{
	adminGetRoles (query: $query)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}

query ($query:[QueryStatementInput])
{
	adminFindRole (query: $query)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}

query ($id:ID)
{
	adminFindRoleById (id:$id)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}

mutation ($payload:AdminUpdateRoleInput!)
{
	adminUpdateRole (payload:$payload)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}

mutation ($id:ID!)
{
	adminDeleteRoleById (id:$id)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}

mutation ($query:[QueryStatementInput])
{
	adminDeleteRoles (query: $query)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}

