graphql.query:
query ($query:QueryStatement $constraint:QueryStatement)
{
	iamPaginateRoles (query:$query constraint:$constraint)
	{
		total
		count
		rows
	}
}

query ($query:QueryStatement)
{
	iamGetRoles (query: $query)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}

query ($query:QueryStatement)
{
	iamFindRole (query: $query)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}

query ($id:ID)
{
	iamFindRoleById (id:$id)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}

mutation ($payload:IamUpdateRoleInput!)
{
	iamUpdateRole (payload:$payload)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}

mutation ($id:ID!)
{
	iamDeleteRoleById (id:$id)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}

mutation ($query:QueryStatement)
{
	iamDeleteRoles (query: $query)
	{
		id
		name
		isMaster
		createdAt
		updatedAt
	}
}
