graphql.query:
query ($query:QueryStatement $constraint:QueryStatement)
{
	oAuthPaginateRefreshTokens (query:$query constraint:$constraint)
	{
		total
		count
		rows
	}
}

query ($query:QueryStatement)
{
	oAuthGetRefreshTokens (query: $query)
	{
		id
		accessTokenId
		token
		isRevoked
		expiresAt
		createdAt
		updatedAt
	}
}

