graphql.query:
mutation ($payload:OAuthCreateRefreshTokenInput!)
{
	oAuthCreateRefreshToken (payload:$payload)
	{
		id
		token
		isRevoked
		expiresAt
		createdAt
		updatedAt
	}
}

